rect.style:
fill:param(color_relleno) #f0f0f0;  /* Parámetro de Relleno principal */
stroke:param(color_contorno) #333333; /* Parámetro de Contorno */
stroke-width:5;

circle.style:
fill:white;
stroke:param(color_contorno) #007bff; /* Usa el color de Contorno principal */
stroke-width:2;

line.style:
stroke:param(color_aguja) #ff0000;   /* Nuevo parámetro: color_aguja */
stroke-width:3;
stroke-linecap:round;
